import React, { useState, useEffect } from 'react';
import connect from '@vkontakte/vk-connect';
import View from '@vkontakte/vkui/dist/components/View/View';
import '@vkontakte/vkui/dist/vkui.css';
import { Tabbar, TabbarItem, Panel, PanelHeader, Button } from '@vkontakte/vkui';
import Epic from '@vkontakte/vkui/dist/components/Epic/Epic.js';
import Icon28MessageOutline from '@vkontakte/icons/dist/28/message_outline.js';
import Icon28SearchOutline from '@vkontakte/icons/dist/28/search_outline.js';
import Icon28NewsfeedOutline from '@vkontakte/icons/dist/28/newsfeed_outline.js';

const Theme = () => {
    const [scheme, SetScheme] = useState("space_gray")
}
function UpdateTheme() {
    if(scheme === "bright_light" || scheme === "client_light") {
        SetScheme('space_gray');
        connect.send("VKWebAppSetViewSettings", {"status_bar_style": "light", "action_bar_color": "#000"});
    }
    else if(scheme === "space_gray" || scheme === "client_dark"){
        SetScheme('bright_light');
        connect.send("VKWebAppSetViewSettings", {"status_bar_style": "dark", "action_bar_color": "#fff"});
    }
}

class App extends React.Component {
    constructor (props) {
        super(props);

        this.state = {
          activeStory: 'feed'
        };
        this.onStoryChange = this.onStoryChange.bind(this);
      }
    
      onStoryChange (e) {
        this.setState({ activeStory: e.currentTarget.dataset.story })
      }
    render() {
      return(
        <Epic activeStory={this.state.activeStory} tabbar={
            <Tabbar>
              <TabbarItem
                onClick={this.onStoryChange}
                selected={this.state.activeStory === 'feed'}
                data-story="feed"
                text="Новости"
              ><Icon28NewsfeedOutline /></TabbarItem>
              <TabbarItem
                onClick={this.onStoryChange}
                selected={this.state.activeStory === 'discover'}
                data-story="discover"
                text="Поиск"
              ><Icon28SearchOutline /></TabbarItem>
              <TabbarItem
                onClick={this.onStoryChange}
                selected={this.state.activeStory === 'messages'}
                data-story="messages"
                label="12"
                text="Сообщения"
              ><Icon28MessageOutline /></TabbarItem>
            </Tabbar>
          }>
            <View id="feed" activePanel="feed">
              <Panel id="feed">
                <PanelHeader>Новости</PanelHeader>
                <img height="100%" width="100%" src="https://sun9-63.userapi.com/c857020/v857020827/13946f/aMol-HoR2Fw.jpg"/>
                <Button size="xl" mode="secondary" marginTop="15px">Перейти в группу</Button>
              </Panel>
            </View>
            <View id="discover" activePanel="discover">
              <Panel id="discover">
                <PanelHeader>Поиск</PanelHeader>
              </Panel>
            </View>
            <View id="messages" activePanel="messages">
              <Panel id="messages">
                <PanelHeader>Сообщения</PanelHeader>
              </Panel>
            </View>
          </Epic>
        )
      }
    }
    export default App;
