import React from 'react';
import {View} from '@vkontakte/vkui';
import '@vkontakte/vkui/dist/vkui.css';

import FileUploader from 'react-firebase-file-uploader';
import firebase from 'Desktop/vkapp/nebuskins/src/firebase.js'


class ImageUploaderSkins extends React.Component {
    
    state = {
     image: '',
     imageURL: '',
     progress: 0
}

handleUploadStart = () => {
    
    this.setState({
        progress: 0
    })
}

handleUploadSuccess = filename => {
    this.setState({
        image: filename,
        progress: 100
    })

    firebase.storage().ref('avatars').child(filename).getDownloadURL()
    .then(url => this.setState({
        imaheURL: url
    }))

}

render() {
    return (
        <View id="file_uploader">
        <FileUploader
            accept="image/*" 
            name='image'
            storageRef={firebase.storage().ref('avatars')}
            onUploadStart={this.handleUploadStart}
            onUploadSuccess={this.handleUploadSuccess}
/>
</View>
    )
}
}

export default ImageUploaderSkins;
