import React from 'react';
import bridge from '@vkontakte/vk-bridge';
import '@vkontakte/vkui/dist/vkui.css';
import { View, Panel, PanelHeader, Button, Cell, Avatar, Div, Placeholder, Separator, Gallery, Group, Header, Counter } from '@vkontakte/vkui';
import '@vkontakte/vkui/dist/vkui.css';

import Icon16Add from '@vkontakte/icons/dist/16/add';
import Icon24Smile from '@vkontakte/icons/dist/24/smile';


class Feed extends React.Component {
    constructor (props) {

        super(props);
  
        this.state = {
          slideIndex: 0
        }
      }
    
render() {

    return (

<View id="feed"  activePanel="feed">
<Panel id="feed">
  <PanelHeader>Главная</PanelHeader>
  <Cell
size="l"
description="Группа сервиса"
before={<Avatar src="https://sun9-24.userapi.com/c856520/v856520080/13a615/MOxzZ-wuRFM.jpg"/>}
bottomContent={<Button before={<Icon16Add/>} onClick={() => bridge.send("VKWebAppJoinGroup", {"group_id": 95380950})}disabled>Вступить</Button>} >Nebulous</Cell>
<Div>
<Placeholder
             action={<Button before={<Icon24Smile/>} href="https://vk.com/nebulous" size="l">Группа разработчиков</Button>}>
          <img src="https://vk.com/sticker/1-20227-256" />
        </Placeholder>
        </Div>
        <Group header={<Header mode="secondary">Я просто играюсь...</Header>}>
              <Gallery
                slideWidth="90%"
                align="center"
                style={{ height:200 }}
                slideIndex={this.state.slideIndex}
                onChange={slideIndex => this.setState({slideIndex})}
              >
                <img src="https://sun9-23.userapi.com/c857032/v857032907/175818/gNvtHZJ3a7s.jpg"/>
                <img src="https://sun9-47.userapi.com/QVxZ0z52lu6aM5r12cuDY3LJVcLIJ84BZdWo-Q/aY1_stMf0c0.jpg"/>
                <img src="https://sun9-46.userapi.com/x2oF0Fnf4EKF09F3XnTTiWldMdiZelCFoDTp9A/_-ysJP7s5hY.jpg"/>
                <img src="https://sun9-5.userapi.com/VnGQw8Fl50kNs4ujoXuvGnLGveHkdPyxIf5uLw/u5s94NZG-cQ.jpg"/>
                <img src="https://sun9-44.userapi.com/hYX63VXUWxdWtHW4Gkl8fj0UnI5WJV1lF-HUVQ/Wg8Le4aGpY4.jpg"/>
                <img src="https://sun9-37.userapi.com/c854016/v854016907/22d540/yT4tyIUP6cg.jpg"/>
                <img src="https://sun9-58.userapi.com/c856524/v856524907/176bd5/wzfPkrwkySQ.jpg"/>
              </Gallery>
              <Div>
                <Button onClick={() => this.setState({slideIndex: this.state.slideIndex === 6 ? 0 : this.state.slideIndex + 1 })}>Следующая картинка</Button>
              </Div>
            </Group>
</Panel>
</View>
    )
}
}
export default Feed;
