import React, { useEffect, useState } from 'react';
import View from '@vkontakte/vkui/dist/components/View/View';
import connect from '@vkontakte/vk-connect';
import '@vkontakte/vkui/dist/vkui.css';
import { Tabbar, TabbarItem, Panel, PanelHeader, Button, Cell, Switch, ConfigProvider } from '@vkontakte/vkui';
import Epic from '@vkontakte/vkui/dist/components/Epic/Epic.js';
import Icon28SettingsOutline from '@vkontakte/icons/dist/28/settings_outline.js';
import Icon28AddOutline from '@vkontakte/icons/dist/28/add_outline.js';
import Icon28NewsfeedOutline from '@vkontakte/icons/dist/28/newsfeed_outline.js';
import Icon24ShareOutline from '@vkontakte/icons/dist/24/share_outline.js';

class App extends React.Component {
  constructor(props) {
      super(props);
      this.state = {
          activePanel: 'home', // Ставим начальную панель
          history: ['home'], // Заносим начальную панель в массив историй.
          scheme: "bright_light" // Если не придет тема - ставим эту по умолчанию.
      };
  }

  componentDidMount() {
      connect.subscribe(({ detail: { type, data }}) => { // Подписываемся на события.
    if (type === 'VKWebAppUpdateConfig') { // Получаем тему клиента.
              this.setState({scheme: data.scheme})
          }
      })
      window.addEventListener('popstate', () => this.goBack()); //  Добавляем обработчик события изменения истории для работы аппаратных кнопок.
  }

  UpdateTheme() {
      if(this.state.scheme === "bright_light" || this.state.scheme === "client_light"){ // Если в стейте эти темы: 
          this.setState({scheme: 'space_gray'}); // меняем тему на альтернативную.
          connect.send("VKWebAppSetViewSettings", {"status_bar_style": "light", "action_bar_color": "#000"}); // Устанавливаем цвет статус бара на белый и экшен бара на черный.
} else if(this.state.scheme === "space_gray" || this.state.scheme === "client_dark") {
          this.setState({scheme: 'bright_light'}); // меняем тему на альтернативную.
          connect.send("VKWebAppSetViewSettings", {"status_bar_style": "dark", "action_bar_color": "#fff"}); // Устанавливаем цвет статус бара на черный и экшен бара на белый.
      }
   }
  
  render() {
      return(
          // scheme - это тема, которую мы ставим в приложением
          <ConfigProvider isWebView={true} scheme={this.state.scheme}> 
          <View 
              activePanel={this.state.activePanel} // Какая панель будет активирована.
              history={this.state.history} // Массив с историей.
              onSwipeBack={this.goBack} // При свайпе выполнять функцию goBack.
          >
              <Epic activeStory={this.state.activeStory} scheme={this.state.scheme} tabbar={
          <Tabbar>
            <TabbarItem
              onClick={this.onStoryChange}
              selected={this.state.activeStory === 'feed'}
              data-story="feed"
              text="Главная"
            ><Icon28NewsfeedOutline /></TabbarItem>
            <TabbarItem
              onClick={this.onStoryChange}
              selected={this.state.activeStory === 'add_skin'}
              data-story="add_skin"
              text="Добавить скин"
            ><Icon28AddOutline /></TabbarItem>
            <TabbarItem
              onClick={this.onStoryChange}
              selected={this.state.activeStory === 'settings'}
              data-story="settings"
              text="Настройки"
            ><Icon28SettingsOutline /></TabbarItem>
          </Tabbar>
        }>
          <View id="feed" activePanel="feed">
            <Panel id="feed">
              <PanelHeader>Главная</PanelHeader>
              <img height="100%" width="100%" margintop="15px" src="https://sun9-63.userapi.com/c857020/v857020827/13946f/aMol-HoR2Fw.jpg" alt="desc"/>
              <Button before={<Icon24ShareOutline/>} size="xl" mode="secondary" href="https://vk.com/nebulous">Перейти в группу</Button>
            </Panel>
          </View>
          <View id="add_skin" activePanel="add_skin">
            <Panel id="add_skin">
              <PanelHeader>Добавить скин</PanelHeader>
              <Button onClick={() => this.UpdateTheme()}>Альтернативная тема</Button>
            </Panel>
          </View>
          <View id="settings" activePanel="settings">
            <Panel id="settings">
              <PanelHeader>Настройки</PanelHeader>
              <Cell asideContent={<Switch disabled/>}>
               Темная тема
        </Cell>
            </Panel>
          </View>
      </Epic>
      )
    }
  }
          </View>
          </ConfigProvider>
      );
  }
}
  export default App;
