import React from 'react';
import '@vkontakte/vkui/dist/vkui.css';
import connect from '@vkontakte/vk-connect';
import { View, Panel, Group, PanelHeader, CellButton, PanelHeaderBack, Text, Button, Header, Div, Root } from '@vkontakte/vkui';

import Settings from './Settings';
import MainProfile from './MainProfile';

class Profile extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            activePanel: 'panel1',
            fetchedUser: null
          }
        }

componentDidMount () {
    connect.send("VKWebAppGetUserInfo", {}).then(e => this.setState({fetchedUser: e.data}))
}

            render() {
                return (
                  <View activePanel={this.state.activePanel}>
                    <Panel id="panel1">
                      <PanelHeader>Профиль</PanelHeader>
                      <Group>
                        <MainProfile fetchedUser={this.state.fetchedUser} />
                        <CellButton onClick={ () => this.setState({ activePanel: 'panel2' }) }>
                          Настройки
                        </CellButton>
                      </Group>
                    </Panel>
                    <Panel id="panel2">
                    <PanelHeader left={<PanelHeaderBack onClick={ () => this.setState({ activePanel: 'panel1' }) }/>} >Настройки</PanelHeader>
                    <Settings/>
                    </Panel>
                    <Panel id="panel3">
                      <PanelHeader>Panel 3</PanelHeader>
                      <Group>
                        <CellButton onClick={ () => this.setState({ activePanel: 'panel1' }) }>
                          Back to panel 1
                        </CellButton>
                      </Group>
                    </Panel>
                  </View>
                )
              
}}
export default Profile;
