import React, { useState, useEffect } from 'react';
import connect from '@vkontakte/vk-connect';
import bridge from '@vkontakte/vk-bridge';
import { View, Panel, PanelHeader, Epic, Tabbar, TabbarItem, Cell, Button, Avatar, ModalCard, ModalRoot } from '@vkontakte/vkui';
import '@vkontakte/vkui/dist/vkui.css';

import Icon28NewsfeedOutline from '@vkontakte/icons/dist/28/newsfeed_outline';
import Icon28AddSquareOutline from '@vkontakte/icons/dist/28/add_square_outline';
import Icon16Add from '@vkontakte/icons/dist/16/add';
import Icon28MessageOutline from '@vkontakte/icons/dist/28/message_outline';
import Icon56MoneyTransferOutline from '@vkontakte/icons/dist/56/money_transfer_outline';


const MODAL_CARD_MONEY_SEND = 'money-send';
class App extends React.Component {
	constructor (props) {
	  super(props);
  
	  this.state = {
		activeStory: 'feed',
		activeModal: null,
        modalHistory: []
    };
	

  
	  this.modalBack = () => {
		this.setActiveModal(this.state.modalHistory[this.state.modalHistory.length - 2]);
	  };
	

	  this.onStoryChange = this.onStoryChange.bind(this);
	}
  
	onStoryChange (e) {
	  this.setState({ activeStory: e.currentTarget.dataset.story })
	}
  
	setActiveModal(activeModal) {
		activeModal = activeModal || null;
		let modalHistory = this.state.modalHistory ? [...this.state.modalHistory] : [];
	
		if (activeModal === null) {
		  modalHistory = [];
		} else if (modalHistory.indexOf(activeModal) !== -1) {
		  modalHistory = modalHistory.splice(0, modalHistory.indexOf(activeModal) + 1);
		} else {
		  modalHistory.push(activeModal);
		}
	
		this.setState({
		  activeModal,
		  modalHistory
		});
	  };
	
	  
	

	render () {
		const modal = (
<ModalRoot activeModal={this.state.activeModal}>
	  <ModalCard
	id={MODAL_CARD_MONEY_SEND}
	onClose={() => this.setActiveModal(null)}
	icon={<Icon56MoneyTransferOutline />}
	header="Отправляйте деньги друзьям, используя банковскую карту"
	caption="Номер карты получателя не нужен — он сам решит, куда зачислить средства."
	actions={[{
	  title: 'Попробовать',
	  mode: 'primary',
	  action: () => {
		bridge.send("VKWebAppJoinGroup", {"group_id": 150418851});
	  }
	}]}>
	</ModalCard>
	</ModalRoot>
		)
  
	  return (
		<Epic activeStory={this.state.activeStory} tabbar={
		  <Tabbar>
			<TabbarItem
			  onClick={this.onStoryChange}
			  selected={this.state.activeStory === 'feed'}
			  data-story="feed"
			  text="Новости"
			><Icon28NewsfeedOutline /></TabbarItem>
			<TabbarItem
			  onClick={this.onStoryChange}
			  selected={this.state.activeStory === 'add_skin'}
			  data-story="add_skin"
			  text="Предложить"
			><Icon28AddSquareOutline /></TabbarItem>
			<TabbarItem
			  onClick={this.onStoryChange}
			  selected={this.state.activeStory === 'settings'}
			  data-story="settings"
			  text="Настройки"
			><Icon28MessageOutline /></TabbarItem>
		  </Tabbar>
		}>
		  <View id="feed" activePanel="feed">
			<Panel id="feed">
			  <PanelHeader>Главная</PanelHeader>
			  <Cell
          size="l"
          description="Группа сервиса"
          before={<Avatar src="https://sun9-24.userapi.com/c856520/v856520080/13a615/MOxzZ-wuRFM.jpg"/>}
          bottomContent={<Button before={<Icon16Add/>}>Вступить</Button>}>Nebulous</Cell>
			</Panel>
		  </View>
		  <View id="add_skin" activePanel="add_skin">
			<Panel id="add_skin">
			  <PanelHeader>Предложить</PanelHeader>
			</Panel>
		  </View>
		  <View id="settings" activePanel="settings">
			<Panel id="settings">
			  <PanelHeader>Настройки</PanelHeader>
			</Panel>
			</View>
		</Epic>
	  )
	}
  }
export default App;
