import React from 'react';
import firebase from 'firebase';
import { Button, Div, Input, Card, CardGrid, Text, Separator, Avatar, Banner } from '@vkontakte/vkui';
import '@vkontakte/vkui/dist/vkui.css';

function TestFeed() {
    const [skins, setSkins] = React.useState([])

    React.useEffect(() => {
        const fetchData = async () => {
            const db = firebase.firestore()
            const data = await db.collection("skins").get()
            setSkins(data.docs.map(doc => doc.data()))
        }
        fetchData()
    }, [])

    return (
     <React.Fragment> 
                 
            {skins.map(skin => (
<Div key={skin.id}>
<Banner>Предложил: {skin.nickname}<br/>
ID игрока: {skin.player_id}<br/>
Категория: {skin.category}</Banner>

 <Banner
        before={<Avatar size={96} mode="image" src="https://sun9-63.userapi.com/yOEQYPHrNHjZEoanbqPb65HPl5iojmiLgLzfGA/W3geVMMt8TI.jpg" />}
        header={skin.nickname}
        subheader={skin.player_id}
        asideMode={skin.category}
        actions={<Button>Подробнее</Button>}
      />
</Div>
))}
        </React.Fragment>
    );
}

export default TestFeed;
