import React, { useEffect, useState } from 'react';
import { Button } from '@vkontakte/vkui';


const Counter = () => {
  // сделайте состояние
  const [counter, setCounter] = useState(0);
  
  // сделайте обработчик для кнопки
  const onClick = (event) => {
    // Здесь изменяйте состояние <==
    // Как изменять состояние будете, какой функцией? Да. Пишите ниже. +1?
    // Вам же надо к текущему состоянию прибавить +1. Текущее состояние в какой переменной? Используйте её.
    // Где будете прибавлять? Да. Прибавляйте.
    // Да, правильно. Это победа. Не совсем. Лучше внутрь функции setCounter передавать функцию, то точно победа. Вот так:
    setCounter(counter => counter + 1)
    // оба варианта правильные, но с функцией лучше
  }
  // этот обработчик нужен для кнопки, он будет инкрементировать счётчик

  // добавьте хук эффекта, функцию задайте
  useEffect(() => {
    // добавьте оператор ветвления и проверяйте счётчик на нужное значение
    if(counter === 10) {
    // выведите в консоль: ура получилось
        console.log('Ура, получилось.....')
        // в принципе все, тут будете открывать свою панель (это я разберусь как)
        // вопросы есть?
    }
    
    // задайте зависимость вторым параметром в списке и положите туда наш счётчик Как это сделать?
  }, [counter])

  // вернуть кнопку
  return (
    // установите onClick на кнопку <==
    // хорошо
    <Button onClick={onClick} >Нажми меня!</Button>
  )
}

export default Counter;
