import React, { useState, useEffect } from 'react';
import connect from '@vkontakte/vk-connect';
import View from '@vkontakte/vkui/dist/components/View/View';
import '@vkontakte/vkui/dist/vkui.css';
import { Tabbar, TabbarItem } from '@vkontakte/vkui';
import Icon28NewsFeed from '@vkontakte/icons/dist/28/newsfeed.js';
import Icon28MessageOutline from '@vkontakte/icons/dist/28/message_outline.js';
import Icon28SearchOutline from '@vkontakte/icons/dist/28/search_outline.js';

class App extends React.Component {
    constructor (props) {
        super(props);
    
        this.state = {
          activeStory: 'more'
        };
        this.onStoryChange = this.onStoryChange.bind(this);
      }
    
      onStoryChange (e) {
        this.setState({ activeStory: e.currentTarget.dataset.story })
      }
    render() {
      return(
          <View activeStory={this.state.activeStory} tabbar={
              <Tabbar>
              <TabbarItem
            onClick={this.onStoryChange}
            selected={this.state.activeStory === 'feed'}
            data-story="feed"
            text="Новости"
          ><Icon28NewsFeed/></TabbarItem>
           <TabbarItem
            onClick={this.onStoryChange}
            selected={this.state.activeStory === 'discover'}
            data-story="discover"
            text="Поиск"
          ><Icon28SearchOutline /></TabbarItem>
          <TabbarItem
            onClick={this.onStoryChange}
            selected={this.state.activeStory === 'messages'}
            data-story="messages"
            label="13"
            text="Сообщения"
          ><Icon28MessageOutline /></TabbarItem>
              </Tabbar>
          }>
           <View id="feed" activePanel="feed">
          <Panel id="feed">
            <PanelHeader>Новости</PanelHeader>
          </Panel>
        </View>
        <View id="discover" activePanel="discover">
          <Panel id="discover">
            <PanelHeader>Поиск</PanelHeader>
          </Panel>
        </View>
        <View id="messages" activePanel="messages">
          <Panel id="messages">
            <PanelHeader>Сообщения</PanelHeader>
          </Panel>
        </View>
          </View>
      )
    }
  }
export default App;
