import React from 'react';
import '@vkontakte/vkui/dist/vkui.css';
import { View, IS_PLATFORM_ANDROID, IS_PLATFORM_IOS, Input, Panel, File, Link, Checkbox, PanelHeader, Div, Select, Placeholder, Button, ModalRoot, PanelHeaderButton, ModalPage, ModalPageHeader, FormLayout } from '@vkontakte/vkui';

import Icon28PictureStackOutline from '@vkontakte/icons/dist/28/picture_stack_outline';
import Icon24Done from '@vkontakte/icons/dist/24/done';
import Icon24Cancel from '@vkontakte/icons/dist/24/cancel';
import Icon24Camera from '@vkontakte/icons/dist/24/camera';

const MODAL_PAGE_SUBMIT = 'add_skin_submit';

class AddSkin extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      activeModal: null,
      modalHistory: [],
      game_id: '',
      regexp : /^[0-9\b]+$/
    };
  
    this.onHandleGameIDChange = this.onHandleGameIDChange.bind(this);

    this.modalBack = () => {
      this.setActiveModal(this.state.modalHistory[this.state.modalHistory.length - 2]);
    };
  }

  onHandleGameIDChange = e => {
    let game_id = e.target.value;

    // if value is not blank, then test the regex
    if (game_id === '' || this.state.regexp.test(game_id)) {
        this.setState({ [e.target.name]: game_id })
    }
};

  setActiveModal(activeModal) {
    activeModal = activeModal || null;
    let modalHistory = this.state.modalHistory ? [...this.state.modalHistory] : [];

    if (activeModal === null) {
      modalHistory = [];
    } else if (modalHistory.indexOf(activeModal) !== -1) {
      modalHistory = modalHistory.splice(0, modalHistory.indexOf(activeModal) + 1);
    } else {
      modalHistory.push(activeModal);
    }

    this.setState({
      activeModal,
      modalHistory
    });
  };
    

  render() {
   const modal = (
      <ModalRoot
        activeModal={this.state.activeModal}
        onClose={this.modalBack}>
          <ModalPage
          id={MODAL_PAGE_SUBMIT}
          onClose={this.modalBack}
          header={
            <ModalPageHeader
              left={IS_PLATFORM_ANDROID && <PanelHeaderButton onClick={this.modalBack}><Icon24Cancel /></PanelHeaderButton>}
              right={<PanelHeaderButton onClick={this.modalBack}>{IS_PLATFORM_IOS ? 'Готово' : <Icon24Done />}</PanelHeaderButton>}
            >
              Предложить скин
            </ModalPageHeader>
          }> 
          <FormLayout>
            <Input maxLength="16" top="Введите ваш никнейм" placeholder="Введите никнейм" />
            <Input maxLength="7" top="Введите ваш игровой ID" type="tel" placeholder="Введите ID" 
                    value={this.state.game_id}
                    onChange={this.onHandleGameIDChange} />
            <Select top="Категория" placeholder="Выберите категорию скина">
              <option value="d">Обычные скины</option>
              <option value="a">Питомцы</option>
              <option value="p">Частицы</option>
            </Select>
            <File top="Загрузите фото скина" before={<Icon24Camera />} size="l">Открыть галерею</File>
            <Checkbox>Я согласен со всем, что вы <Link>там</Link> понаписали</Checkbox>
            <Button size="xl">Отправить</Button>
            </FormLayout>
         </ModalPage>
        </ModalRoot>
   );
      return (
          <View id="add_skin" activePanel="add_skin" modal={modal}>
          <Panel id="add_skin">
            <PanelHeader>Предложить</PanelHeader>
            <Div>
        <Placeholder
          icon={<Icon28PictureStackOutline width={128} height={128} />}
          header="Предложить свой скин"
  action={<Button onClick={() => this.setActiveModal(MODAL_PAGE_SUBMIT)} mode="commerce" size="l">Предложить скин</Button>}
          >
          <h4>Привет! В данном разделе ты можешь предложить свой собственный скин для каталога приложения!
          Владелец каждого скина, который будет принят в каталог приложения получит от 2,500 до 5,000
          плазмы от администраторов сообщества Nebulous.</h4>
        </Placeholder>
        </Div>
          </Panel>
        </View>
       
        )
      }
    }

export default AddSkin;
