import React from 'react';
import firebase from 'firebase';
import { Button, Div, Input, Card, CardGrid, Text } from '@vkontakte/vkui';
import '@vkontakte/vkui/dist/vkui.css';

function AddSkinForm() {
    const [skins, setSkins] = React.useState([])
    const [newSkinNickname, setNewSkinNickname] = React.useState()

    React.useEffect(() => {
        const fetchData = async () => {
            const db = firebase.firestore()
            const data = await db.collection("skins").get()
            setSkins(data.docs.map(doc => doc.data()))
        }
        fetchData()
    }, [])

    const onCreate = () => {
        const db = firebase.firestore()
        db.collection('skins').add({nickname: newSkinNickname})
    }

    return (
        <Div>
            <Input value={newSkinNickname} disabled onChange={(e) => setNewSkinNickname(e.target.value) }/>
            <Button disabled onClick={onCreate}>Создать</Button>
        <CardGrid>
        <Card  style={{ height: 96 }} size="l">
        
            {skins.map(skin => (
    <Text style={{ fontSize: 25, textAlign: 'right' }} key={skin.nickname}>{skin.nickname}</Text>
        ))}
        {skins.map(skin => (
    <Text key={skin.player_id}>{skin.player_id}</Text>
        ))}
          </Card>
        </CardGrid>
        <FormLayout>
            <Input maxLength="16" top="Введите ваш никнейм" placeholder="Введите никнейм" />
            <Input maxLength="8" top="Введите ваш игровой ID" type="tel" placeholder="Введите ID" />
            <Select top="Категория" placeholder="Выберите категорию скина">
              <option value="d">Обычные скины</option>
              <option value="a">Питомцы</option>
              <option value="p">Частицы</option>
            </Select>
            <File top="Загрузите фото скина" before={<Icon24Camera />} size="l">Открыть галерею</File>
            <Checkbox>Я согласен со всем, что вы <Link>там</Link> понаписали</Checkbox>
            <Button size="xl">Отправить</Button>
            </FormLayout>
</Div>
    );
}

export default AddSkinForm;
