import React from 'react';
import bridge from '@vkontakte/vk-bridge';
import connect from '@vkontakte/vk-connect';
import '@vkontakte/vkui/dist/vkui.css';
import { View, Panel, PanelHeader, Button, Cell, Avatar, Div, Placeholder, Text, ConfigProvider } from '@vkontakte/vkui';
import Icon16Add from '@vkontakte/icons/dist/16/add';
import Icon24Smile from '@vkontakte/icons/dist/24/smile';
import TestFeed from './TestFeed';


class Feed extends React.Component {
    constructor (props) {

        super(props);
        this.state = {
          activePanel: 'feed', 
          history: ['feed'], 
      };
  }
  goToPage(name) { // Функция, которая переносит нас на следущую панель 
    window.history.pushState({panel: name}, name); //  Создаём новую запись в истории браузера
    this.setState({
        activePanel: name,  // Меняем активную панель на second
        history: [...this.state.history, name] // Добавляем панель в историю
    })
}
goBack = () => { // Функция, которая переносит нас на панель назад.
  const history = this.state.history;
  if(history.length === 1) { // Если в массиве одно значение:
      connect.send("VKWebAppClose"); // отправляем connect на закрытие сервиса.
  } else if (history.length > 1) { // Если в массиве больше одного значения:
      history.pop() // удаляем последний элемент в массиве.
      this.setState({history: history, activePanel: history[history.length - 1]}) // Изменяем массив с иторией и меняем активную панель.
  }
}
componentDidMount() {
  
  window.addEventListener('popstate', () => this.goBack()); //  Добавляем обработчик события изменения истории для работы аппаратных кнопок.
}
render() { 

    return (
<ConfigProvider isWebView={true} scheme={this.state.scheme}> 
            <View 
                activePanel={this.state.activePanel} // Какая панель будет активирована.
                history={this.state.history} // Массив с историей.
                onSwipeBack={this.goBack} // При свайпе выполнять функцию goBack.
            >
           <Div style={{display: 'center' }}>
  <Button size="xl" style={{marginBottom: '10px', blockSize: 60 }} mode="primary"><Text style={{ color: '#ffffff', fontWeight: 700, textAlign: 'left', fontSize: 20 }}>Обычные скины</Text></Button>
<Button size="xl" after={<Icon24Smile style={{ width: 40 }} />} style={{marginBottom: '10px', blockSize: 60 }} mode="primary"><Text style={{ fontWeight: 700, textAlign: 'left', fontSize: 20 }}>Питомцы</Text></Button>
<Button size="xl" style={{marginBottom: '10px', blockSize: 60  }} mode="primary"><Text style={{ color: '#ffffff', fontWeight: 700, textAlign: 'left', fontSize: 20 }}>Частицы</Text></Button>
     </Div>
            </View>
            </ConfigProvider>
    )
}
}
export default Feed;
