import React from 'react';
import firebase from 'firebase';
import PropTypes from 'prop-types';
import { Div, Avatar, Banner, PanelHeaderButton, Search, List, Button, Header, Group, PanelHeader, Panel, Cell, PanelHeaderBack } from '@vkontakte/vkui';
import "@vkontakte/vkui/dist/vkui.css";

import Icon24ThumbUp from '@vkontakte/icons/dist/24/thumb_up';
import Icon28AddOutline from '@vkontakte/icons/dist/28/add_outline';

function TestFeed({id}) {
    const [skins, setSkins] = React.useState([])

    React.useEffect(() => {
        const fetchData = async () => {
            const db = firebase.firestore()
            const data = await db.collection("skins").get()
            setSkins(data.docs.map(doc => doc.data()))
        }
        fetchData()
    }, [])


    return (
     <React.Fragment id={id}> 
                 
            {skins.map(skin => (
<Div key={skin.id}>
 <Banner style={{ blockSize: 95 }} size="l"
        before={<Avatar size={96} mode="image" src={skin.image} />}
        header={<React.Fragment>Предложил: {skin.nickname}</React.Fragment>}
        subheader={<React.Fragment>ID игрока: {skin.player_id}<br/>Категория: {skin.category}</React.Fragment>}
        text={<Button after={skin.likes} mode="tertiary" style={{ width: 10, height: 30 }}><Icon24ThumbUp style={{ width: 5, height: 5 }}/></Button>}
      />
</Div>
))}
        </React.Fragment>
	)
			}
	
			TestFeed.propTypes = {
				id: PropTypes.string.isRequired
			}

export default TestFeed;
