import React, { useState, useEffect } from 'react';
import bridge from '@vkontakte/vk-bridge';
import {View, ConfigProvider} from '@vkontakte/vkui/src/components';
import '@vkontakte/vkui/dist/vkui.css';

import firebase from './firebase';

import Home from './panels/NewApp/Home';
import TestFeed from './panels/NewApp/TestFeed';

const App = () => {
	const [activePanel, setActivePanel] = useState('home');
  const [fetchedUser, setUser] = useState(null);
  const [skins, setSkins] = React.useState([])
  const thematics = [
    {id: 1, name: "Аниме"},
    {id: 2, name: "Животные"},
  ];
  const [scheme, SetScheme] = useState("space_gray");

	useEffect(() => {
		bridge.subscribe(({ detail: { type, data }}) => {
			if (type === 'VKWebAppUpdateConfig') {
				const schemeAttribute = document.createAttribute('scheme');
				schemeAttribute.value = data.scheme ? data.scheme : 'client_light';
				document.body.attributes.setNamedItem(schemeAttribute);
			}
		});
		async function fetchData() {
			const user = await bridge.send('VKWebAppGetUserInfo');
			setUser(user);
		}
		fetchData();
  }, []);

  useEffect(() => {
    bridge.subscribe(({ detail: { type, data }}) => {
    if (type === 'VKWebAppUpdateConfig') {
      SetScheme(data.scheme)
    }
  });
  }, []);
  
  
  React.useEffect(() => {
    const fetchData = async () => {
        const db = firebase.firestore()
        const data = await db.collection("skins").get()
        setSkins(data.docs.map(doc => doc.data()))
    }
    fetchData()
}, [])

	const go = e => {
		setActivePanel(e.currentTarget.dataset.to);
	};

	return (
    <ConfigProvider>
		<View activePanel={activePanel}>
			<Home id='home' fetchedUser={fetchedUser} go={go} />
			<TestFeed id='def_skins' thematics={thematics} go={go} />
		</View>
    </ConfigProvider>
	);
}

export default App;
