import React, { useState, useEffect} from 'react';
import connect from '@vkontakte/vk-connect';
import ReactDOM from 'react-dom';
import { Panel, PanelHeader, Epic, Tabbar, TabbarItem, View } from '@vkontakte/vkui';
import '@vkontakte/vkui/dist/vkui.css';

import Icon28NewsfeedOutline from '@vkontakte/icons/dist/28/newsfeed_outline.js';
import Icon28AddOutline from '@vkontakte/icons/dist/28/add_outline.js';

const App = () => {
  const [activePanel, setActivePanel] = useState("main");
  const [scheme, SetScheme] = useState("space_gray")
  const [history, setHistory] = useState(['main'])

  function UpdateTheme() {
    if(scheme === "bright_light" || scheme === "client_light") {
      SetScheme('space_gray');
      connect.send("VKWebAppSetViewSettings", {"status_bar_style": "light", "action_bar_color": "#000"});
    }
    else if(scheme === "space_gray" || scheme === "client_dark"){
      SetScheme('bright_light');
      connect.send("VKWebAppSetViewSettings", {"status_bar_style": "dark", "action_bar_color": "#fff"});
    }
}

function goToPage(name) {
  window.history.pushState({panel: name}, name)
  setActivePanel(name);
  history.push(name)
};

  return (
        <Epic activePanel={activePanel}
             history={history}
             UpdateTheme={UpdateTheme} tabbar={
          <Tabbar>
            <TabbarItem
            onClick= {setHistory}
            selected={activePanel === 'main'}
            data-story="main"
            text="Главная"
            ><Icon28NewsfeedOutline /></TabbarItem>
            <TabbarItem
            onClick={history}
            selected={activePanel === 'add'}
            data-story="add"
            text="Добавить скин"
            ><Icon28AddOutline /></TabbarItem>
            </Tabbar>
            }>
            <View id="main" activePanel="main">
          <Panel id="main">
            <PanelHeader>Новости</PanelHeader>
          </Panel>
        </View>
        <View id="add" activePanel="add">
          <Panel id="add">
            <PanelHeader>Поиск</PanelHeader>
          </Panel>
        </View>
          </Epic>
  )
}
 export default App;
