import React, { useState, useEffect } from 'react';
import connect from '@vkontakte/vk-connect';
import { View, Panel, PanelHeader, Epic, Tabbar, TabbarItem, Cell, Button, Avatar } from '@vkontakte/vkui';
import '@vkontakte/vkui/dist/vkui.css';

import Icon28NewsfeedOutline from '@vkontakte/icons/dist/28/newsfeed_outline';
import Icon28AddSquareOutline from '@vkontakte/icons/dist/28/add_square_outline';
import Icon16Add from '@vkontakte/icons/dist/16/add';
import Icon28MessageOutline from '@vkontakte/icons/dist/28/message_outline';

class App extends React.Component {
	constructor (props) {
	  super(props);
  
	  this.state = {
		activeStory: 'feed'
	  };
	  this.onStoryChange = this.onStoryChange.bind(this);
	}
  
	onStoryChange (e) {
	  this.setState({ activeStory: e.currentTarget.dataset.story })
	}
  
	render () {
  
	  return (
		<Epic activeStory={this.state.activeStory} tabbar={
		  <Tabbar>
			<TabbarItem
			  onClick={this.onStoryChange}
			  selected={this.state.activeStory === 'feed'}
			  data-story="feed"
			  text="Новости"
			><Icon28NewsfeedOutline /></TabbarItem>
			<TabbarItem
			  onClick={this.onStoryChange}
			  selected={this.state.activeStory === 'add_skin'}
			  data-story="add_skin"
			  text="Предложить"
			><Icon28AddSquareOutline /></TabbarItem>
			<TabbarItem
			  onClick={this.onStoryChange}
			  selected={this.state.activeStory === 'settings'}
			  data-story="settings"
			  text="Настройки"
			><Icon28MessageOutline /></TabbarItem>
		  </Tabbar>
		}>
		  <View id="feed" activePanel="feed">
			<Panel id="feed">
			  <PanelHeader>Главная</PanelHeader>
			  <Cell
          size="l"
          description="Группа сервиса"
          before={<Avatar src="https://sun9-24.userapi.com/c856520/v856520080/13a615/MOxzZ-wuRFM.jpg"/>}
          bottomContent={<Button before={<Icon16Add/>}>Вступить</Button>}>Nebulous</Cell>
			</Panel>
		  </View>
		  <View id="add_skin" activePanel="add_skin">
			<Panel id="add_skin">
			  <PanelHeader>Предложить</PanelHeader>
			</Panel>
		  </View>
		  <View id="settings" activePanel="settings">
			<Panel id="settings">
			  <PanelHeader>Настройки</PanelHeader>
			</Panel>
			</View>
		</Epic>
	  )
	}
  }

export default App;
