import React, { useState, useEffect, useCallback } from 'react';
import ReactDOM from 'react-dom';
import connect from '@vkontakte/vk-connect';
import View from '@vkontakte/vkui/dist/components/View/View';
import ConfigProvider from '@vkontakte/vkui/dist/components/ConfigProvider/ConfigProvider';
import '@vkontakte/vkui/dist/vkui.css';
import { Panel, PanelHeader, Tabbar, TabbarItem, Epic, Button } from '@vkontakte/vkui';
import Icon28AddOutline from '@vkontakte/icons/dist/28/add_outline';
import Icon28NewsfeedOutline from '@vkontakte/icons/dist/28/newsfeed_outline';
import Icon28SettingsOutline from '@vkontakte/icons/dist/28/settings_outline';


const App = () => {
  const [activePanel, setActivePanel] = useState("feed");
	const [scheme, SetScheme] = useState("bright_light")
  const [history, setHistory] = useState(['feed'])
  
  function goToPage(name) {
		window.history.pushState({panel: name}, name)
		setActivePanel(name);
		history.push(name)
	};

	function goBack() {
  		if(history.length === 1){
    		connect.send("VKWebAppClose");
  		} else if(history.length > 1){
			history.pop()
			setActivePanel(history[history.length - 1])
  		}
  }
  this.state = {
    activeStory: 'feed'
  };

	function UpdateTheme() {
			if(scheme === "bright_light" || scheme === "client_light") {
				SetScheme('space_gray');
				connect.send("VKWebAppSetViewSettings", {"status_bar_style": "light", "action_bar_color": "#000"});
			}
			else if(scheme === "space_gray" || scheme === "client_dark"){
				SetScheme('bright_light');
				connect.send("VKWebAppSetViewSettings", {"status_bar_style": "dark", "action_bar_color": "#fff"});
			}
	}

	useEffect(() => {
        window.addEventListener('popstate', () => goBack()); //  Добавляем обработчик события изменения истории для работы аппаратных кнопок.
		connect.subscribe(({ detail: { type, data }}) => {
			if (type === 'VKWebAppUpdateConfig') {
				SetScheme(data.scheme)
			}
		});
	}, []);

    return (
      <ConfigProvider scheme={scheme}>
      <Epic>
        <Tabbar activePanel={activePanel}>
          <TabbarItem
            onClick={history} 
            selected={this.state.activeStory === 'feed'}
            data-story="feed"
            text="Главная"
          ><Icon28NewsfeedOutline /></TabbarItem>
          <TabbarItem
            onClick={history} 
            selected={this.state.activeStory === 'add_skin'}
            data-story="add_skin"
            text="Добавить скин"
          ><Icon28AddOutline /></TabbarItem>
          <TabbarItem
            onClick={history} 
            selected={this.state.activeStory === 'settings'}
            data-story="settings"
            text="Настройки"
          ><Icon28SettingsOutline /></TabbarItem>
        </Tabbar>
        <View id="feed" setActivePanel = 'feed'>
        <Panel id="feed">
        <PanelHeader>Главная</PanelHeader>
        </Panel>
        </View>
        <View id="add_skin" setActivePanel = 'add_skin'>
          <Panel id="add_skin">
          <PanelHeader>Добавить скин</PanelHeader>
          </Panel>
        </View>
        <View id="settings" setActivePanel = 'settings'>
          <Panel id="settings">
          <PanelHeader>Настройки</PanelHeader>
          </Panel>
        </View>
      </Epic>
      </ConfigProvider>
    )
  }

export default App;
