import React from 'react';
import { Panel, PanelHeader, Group, Avatar, Button } from '@vkontakte/vkui';

class MainProfile extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            fetchedUser: null
          }
        }

componentDidMount () {
    connect.send("VKWebAppGetUserInfo", {}).then(e => this.setState({fetchedUser: e.data}))
}

            render() {
                return (
    <Panel fetchedUser={this.state.fetchedUser} id="main_profile">
        {fetchedUser &&
<Group title="Результат из VK Connect">
		before={fetchedUser.photo_200 ? <Avatar src={fetchedUser.photo_200}/> : null}
		description={fetchedUser.city && fetchedUser.city.title ? fetchedUser.city.title : ''}
	>
		{`${fetchedUser.first_name} ${fetchedUser.last_name}`}
</Group>}
    </Panel>
)
        }
    }
export default MainProfile;
